import React, { useState, useEffect, useRef } from 'react';
import "../styles/SnakeGame.css";


const SnakeGame = () => {
  const GAME_PIXEL_COUNT = 40;
  const SQUARE_OF_GAME_PIXEL_COUNT = Math.pow(GAME_PIXEL_COUNT, 2);

  const [gameBoardPixels, setGameBoardPixels] = useState(Array(SQUARE_OF_GAME_PIXEL_COUNT).fill(0));
  const [currentFoodPosition, setCurrentFoodPosition] = useState(null);
  const [snakeCurrentDirection, setSnakeCurrentDirection] = useState(39);
  const [currentSnake, setCurrentSnake] = useState([SQUARE_OF_GAME_PIXEL_COUNT / 2]);
  const [totalFoodAte, setTotalFoodAte] = useState(0);
  const [totalDistanceTravelled, setTotalDistanceTravelled] = useState(0);

  const moveSnakeInterval = useRef(null);

  useEffect(() => {
    createGameBoardPixels();
    createFood();
  }, []);

  useEffect(() => {
    moveSnakeInterval.current = setInterval(moveSnake, 80);
    return () => clearInterval(moveSnakeInterval.current);
  }, [currentSnake, currentFoodPosition, snakeCurrentDirection]);

  useEffect(() => {
    const handleKeyDown = (e) => changeDirection(e.keyCode);
    document.addEventListener('keydown', handleKeyDown);

    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [snakeCurrentDirection]);

  const createGameBoardPixels = () => {
    setGameBoardPixels(Array(SQUARE_OF_GAME_PIXEL_COUNT).fill(0));
  };

  const createFood = () => {
    let newFoodPosition;
    do {
      newFoodPosition = Math.floor(Math.random() * SQUARE_OF_GAME_PIXEL_COUNT);
    } while (gameBoardPixels[newFoodPosition] !== 0);

    setGameBoardPixels((prevPixels) => {
      const newPixels = [...prevPixels];
      newPixels[newFoodPosition] = 'food';
      return newPixels;
    });

    setCurrentFoodPosition(newFoodPosition);
  };

  const changeDirection = (newDirectionCode) => {
    if (newDirectionCode === 37 && snakeCurrentDirection !== 39) {
      setSnakeCurrentDirection(newDirectionCode);
    } else if (newDirectionCode === 38 && snakeCurrentDirection !== 40) {
      setSnakeCurrentDirection(newDirectionCode);
    } else if (newDirectionCode === 39 && snakeCurrentDirection !== 37) {
      setSnakeCurrentDirection(newDirectionCode);
    } else if (newDirectionCode === 40 && snakeCurrentDirection !== 38) {
      setSnakeCurrentDirection(newDirectionCode);
    }
  };

  const moveSnake = () => {
    const newSnake = [...currentSnake];
    let head = newSnake[0];

    switch (snakeCurrentDirection) {
      case 37: // LEFT
        head = head - 1;
        break;
      case 38: // UP
        head = head - GAME_PIXEL_COUNT;
        break;
      case 39: // RIGHT
        head = head + 1;
        break;
      case 40: // DOWN
        head = head + GAME_PIXEL_COUNT;
        break;
      default:
        break;
    }

    if (head === currentFoodPosition) {
      newSnake.unshift(currentFoodPosition);
      createFood();
      setTotalFoodAte((prevFood) => prevFood + 1);
    } else {
      newSnake.unshift(head);
      const tail = newSnake.pop();
      setGameBoardPixels((prevPixels) => {
        const newPixels = [...prevPixels];
        newPixels[tail] = 0;
        return newPixels;
      });
    }

    setCurrentSnake(newSnake);
    setTotalDistanceTravelled((prevDistance) => prevDistance + 1);
  };

  return (
    <div>
      <div id="gameContainer">
        {gameBoardPixels.map((pixel, index) => (
          <div key={index} className={`gameBoardPixel ${pixel}`} />
        ))}
      </div>

      <div id="scoreContainer">
        <div className="scoreBoard">Food: <span id="pointsEarned">{totalFoodAte}</span></div>
        <div className="scoreBoard">Blocks: <span id="blocksTravelled">{totalDistanceTravelled}</span></div>
      </div>

      <div id="onScreenControllers">
        <button id="leftButton">◀️</button>
        <div>
          <button id="upButton">🔼</button>
          <button id="downButton">🔽</button>
        </div>
        <button id="rightButton">▶️</button>
      </div>

      <div className="developerDetails">
        <div>
          {/* ... */}
        </div>
      </div>
    </div>
  );
};

export default SnakeGame;
