import React, { useState, useEffect, useRef } from 'react';
import "../styles/SnakeGame.css";

const SnakeGame = () => {
  const GAME_PIXEL_COUNT = 40;
  const SQUARE_OF_GAME_PIXEL_COUNT = Math.pow(GAME_PIXEL_COUNT, 2);

  const [totalFoodAte, setTotalFoodAte] = useState(0);
  const [totalDistanceTravelled, setTotalDistanceTravelled] = useState(0);
  const [currentFoodPosition, setCurrentFoodPosition] = useState(0);
  const [snakeCurrentDirection, setSnakeCurrentDirection] = useState(39);
  const [currentSnakeHeadPosition, setCurrentSnakeHeadPosition] = useState(SQUARE_OF_GAME_PIXEL_COUNT / 2);
  const [snakeLength, setSnakeLength] = useState(1000);

  const gameBoardRef = useRef(null);

  useEffect(() => {
    // Create game board pixels
    createGameBoardPixels();
    // Create initial food
    createFood();
    // Move snake
    const moveSnakeInterval = setInterval(moveSnake, 80);

    // Cleanup on unmount
    return () => clearInterval(moveSnakeInterval);
  }, []);

  useEffect(() => {
    // Handle keydown event
    const handleKeyDown = (e) => changeDirection(e.keyCode);
    window.addEventListener("keydown", handleKeyDown);
    // Cleanup on unmount
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [changeDirection]);

  const createGameBoardPixels = () => {
    const gamePixelDivs = Array.from({ length: SQUARE_OF_GAME_PIXEL_COUNT }, (_, index) => (
      <div key={index} className="gameBoardPixel" id={`pixel${index + 1}`}></div>
    ));
    gameBoardRef.current.innerHTML = gamePixelDivs;
  };

  const createFood = () => {
    gameBoardRef.current.children[currentFoodPosition].classList.remove("food");

    const newFoodPosition = Math.floor(Math.random() * SQUARE_OF_GAME_PIXEL_COUNT);
    setCurrentFoodPosition(newFoodPosition);
    gameBoardRef.current.children[newFoodPosition].classList.add("food");
  };

  const changeDirection = (newDirectionCode) => {
    if (newDirectionCode === snakeCurrentDirection) return;

    if (newDirectionCode === 37 && snakeCurrentDirection !== 39) {
      setSnakeCurrentDirection(newDirectionCode);
    } else if (newDirectionCode === 38 && snakeCurrentDirection !== 40) {
      setSnakeCurrentDirection(newDirectionCode);
    } else if (newDirectionCode === 39 && snakeCurrentDirection !== 37) {
      setSnakeCurrentDirection(newDirectionCode);
    } else if (newDirectionCode === 40 && snakeCurrentDirection !== 38) {
      setSnakeCurrentDirection(newDirectionCode);
    }
  };

  const moveSnake = () => {
    // Snake movement logic...

    // Update total distance travelled
    setTotalDistanceTravelled((prev) => prev + 1);

    if (currentSnakeHeadPosition === currentFoodPosition) {
      // Update total food ate
      setTotalFoodAte((prev) => prev + 1);
      // Increase Snake length:
      setSnakeLength((prev) => prev + 100);
      createFood();
    }
  };

  return (
    <div>
      <div id="gameContainer" ref={gameBoardRef}></div>

      <div id="scoreContainer">
        <div className="scoreBoard">Food: <span id="pointsEarned">{totalFoodAte}</span></div>
        <div className="scoreBoard">Blocks: <span id="blocksTravelled">{totalDistanceTravelled}</span></div>
      </div>

      <div id="onScreenControllers">
        <button onClick={() => changeDirection(37)}>‚óÄÔ∏è</button>
        <div>
          <button onClick={() => changeDirection(38)}>üîº</button>
          <button onClick={() => changeDirection(40)}>üîΩ</button>
        </div>
        <button onClick={() => changeDirection(39)}>‚ñ∂Ô∏è</button>
      </div>

      <div className="developerDetails">
        {/* Developer details... */}
      </div>
    </div>
  );
};

export default SnakeGame;
